<script>
    function menuSlide(){
        let panel = $('#panel'),
            isOpen = panel.hasClass('to-slide'),
            menu = $('#menu');

        if(isOpen){
            panel.removeClass('to-slide');
        }else{
            panel.addClass('to-slide');
            menu.show();
        }
    }

    $(window).on('click', function(event){
        let panel = $('#panel'),
            isOpen = panel.hasClass('to-slide'),
            menu = document.getElementById('menu'),
            menuBtn = document.getElementById('menu-btn');

        if(!$(event.target).closest('.login-form-mobile').length && event.target != menu && event.target != menuBtn && event.target.tagName  != 'A' && isOpen){
            panel.removeClass('to-slide');
        }
    });
</script>

